    private void checkEmailBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_checkEmailBtnActionPerformed
       
	if(txt_email.getText().equals("") || !txt_email.getText().matches("^.+@.+\\..+$")){
	      JOptionPane.showMessageDialog(this, "Enter valid Email");
	}
	else{
	
	    try {
		Class.forName("com.microsoft.sqlserver.jdbc.SQLServerDriver");
		Connection con = DriverManager.getConnection("jdbc:sqlserver://localhost:1433;databaseName=ExImp;selectMethod=cursor", "sa", "112233");

		String UserIDCheckz = txt_email.getText();
		String sql = "select Email, SecurityQuestion from [User] where Email = ?";
		PreparedStatement pst = con.prepareStatement(sql);
		pst.setString(1, UserIDCheckz);
		ResultSet rs = pst.executeQuery();
		if (rs.next()) {
		    String questionz = rs.getString("SecurityQuestion");
		    txt_securityQuestion.setText(questionz);
		    txt_answer.setText("");
		    txt_password.setText("");
		    txt_confirmPassword.setText("");
		} else {
		    JOptionPane.showMessageDialog(this, "User Email Not Found!");
		    txt_securityQuestion.setText("");
		    txt_answer.setText("");
		    txt_password.setText("");
		    txt_confirmPassword.setText(""); 
		}
	    }
	     catch (Exception e) {
		e.printStackTrace();
	    }
	}
    }//GEN-LAST:event_checkEmailBtnActionPerformed

