    private void ResetPasswordMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_ResetPasswordMouseClicked
        
	if(txt_email.getText().equals("")){
	      JOptionPane.showMessageDialog(this, "Enter Email");
	}
	else if(txt_answer.getText().equals("")){
	      JOptionPane.showMessageDialog(this, "Enter answer");
	}
	else if(txt_password.getText().equals("")){
	      JOptionPane.showMessageDialog(this, "Enter Password");
	}
	else if(txt_confirmPassword.getText().equals("")){
	      JOptionPane.showMessageDialog(this, "Enter Confirm password");
	}
	else if(!txt_confirmPassword.getText().equals(txt_password.getText())){
	      JOptionPane.showMessageDialog(this, "Password Do not Match!");
	}
	else{
	
	    try {
		Class.forName("com.microsoft.sqlserver.jdbc.SQLServerDriver");
		Connection con = DriverManager.getConnection("jdbc:sqlserver://localhost:1433;databaseName=ExImp;selectMethod=cursor", "sa", "112233");

		String UserIDCheckz = txt_email.getText();
		String AnswerZ = txt_answer.getText();
		String passz = txt_password.getText();
		String sql = "select Email, SecurityQuestion, Answer from [User] where Email = ?";
		PreparedStatement pst = con.prepareStatement(sql);
		pst.setString(1, UserIDCheckz);
		ResultSet rs = pst.executeQuery();
		if (rs.next()) {
		    String answerz = rs.getString("Answer");
		    
		    if(AnswerZ.equals(answerz)){
		       String sqlz = "Update [User] set Password = ? where Email = ?";
		       PreparedStatement pstz = con.prepareStatement(sqlz);
		       pstz.setString(1, passz);
		       pstz.setString(2, UserIDCheckz);

		       int rowCount = pstz.executeUpdate();

		       if (rowCount > 0) {
			   JOptionPane.showMessageDialog(this, "Password Updated Successfully!");
			   txt_email.setText("");
			   txt_securityQuestion.setText("");
			   txt_answer.setText("");
			   txt_password.setText("");
			   txt_confirmPassword.setText("");
			   
		       } else {
			   JOptionPane.showMessageDialog(this, "Couldn't Connect to Server!");
		       }
		    }
		    else{
			JOptionPane.showMessageDialog(this, "Your Answer is Incorrect!");
		    }

		} else {
		    JOptionPane.showMessageDialog(this, "User Email Not Found!");
		    txt_answer.setText("");
		    txt_password.setText("");
		    txt_confirmPassword.setText("");
		}
	    }

	     catch (Exception e) {
		e.printStackTrace();
	    }
	}
    }//GEN-LAST:event_ResetPasswordMouseClicked

